import { PixiRenderer } from '@nostalgi2d/engine';

const renderer = new PixiRenderer(canvas, {
  width: 800,
  height: 600,
  backgroundColor: 0x1a1a2e
});

engine.setRenderer(renderer);

methods.example:
const sprite = new Sprite(texture);
renderer.drawSprite(sprite, {
  position: { x: 100, y: 100 },
  rotation: Math.PI / 4,
  scale: { x: 2, y: 2 }
});

const vertices = [
  { x: 0, y: -20 },
  { x: -15, y: 15 },
  { x: 15, y: 15 }
];
renderer.drawMesh(vertices, 0x00FFFF, transform);

// Draw a debug line
renderer.drawLine(
  { x: 0, y: 0 },
  { x: 100, y: 100 },
  0xFF0000,
  2
);

// Draw a filled circle
renderer.drawCircle({ x: 200, y: 150 }, 30, 0x00FF00, true);

// Draw a circle outline
renderer.drawCircle({ x: 200, y: 150 }, 35, 0xFFFFFF, false);

renderer.drawText('Score: 100', { x: 10, y: 10 }, {
  font: 'Arial',
  size: 24,
  color: 0xFFFFFF,
  align: 'left'
});

const camera = new Camera({
  position: { x: 0, y: 0 },
  zoom: 1.0
});
renderer.setCamera(camera);

const screenPos = renderer.worldToScreen(enemy.position);
if (screenPos.x > 0 && screenPos.x < width) {
