import { World } from '@nostalgi2d/engine';

const world = new World();
world.addActor(player);

const enemies = world.query({ 
  has: [TransformComponent, EnemyComponent] 
});

methods.example:
const actor = world.getActor('player-001');
if (actor) {
  console.log('Found player:', actor.name);
}

// Find all actors with transform and sprite components
const visibleActors = world.query({
  has: [TransformComponent, SpriteComponent]
});

// Find actors with either physics or kinematic
const movingActors = world.query({
  hasAny: [PhysicsComponent, KinematicComponent]
});

// Find sprites without physics
const staticSprites = world.query({
  has: [SpriteComponent],
  without: [PhysicsComponent]
});

// Clear the world when loading a new level
world.clear();
loadLevel('level-2');

// Called automatically by the engine
engine.onUpdate((dt) => {
  world.update(dt);
});

// Called automatically by the engine
engine.onRender(() => {
  world.render(renderer);
});

// Find all enemy actors
const enemies = world.findActorsByName('enemy');
enemies.forEach(enemy => {
  enemy.takeDamage(10);
});

// Mark actors as 'player-owned'
player.addTag('player-owned');
playerBullet.addTag('player-owned');

// Find all player-owned entities
const playerEntities = world.findActorsByTag('player-owned');
