import { Engine, World, PixiRenderer } from '@nostalgi2d/engine';

// Initialize the engine with configuration
const engine = new Engine({
  targetFPS: 60,
  fixedTimestep: 1000 / 60, // 60 FPS
  maxFrameSkip: 5
});

// Create a world for entity management
const world = new World();
engine.setWorld(world);

// Set up the renderer
const canvas = document.getElementById('game-canvas') as HTMLCanvasElement;
const renderer = new PixiRenderer(canvas, {
  width: 800,
  height: 600,
  backgroundColor: 0x0a0e1a
});

engine.setRenderer(renderer);

console.log('Engine initialized!');

import { Actor, TransformComponent, SpriteComponent } from '@nostalgi2d/engine';

// Create a player actor
const player = new Actor('player');

// Add a transform component for position and rotation
const transform = new TransformComponent({
  position: { x: 400, y: 300 },
  rotation: 0,
  scale: { x: 1, y: 1 }
});
player.addComponent(transform);

// Add a sprite component for rendering
const sprite = new SpriteComponent({
  width: 32,
  height: 32,
  color: 0x00ffff // Cyan color
});
player.addComponent(sprite);

// Add the actor to the world
world.addActor(player);

console.log('Player actor created!');

// Define the update function
function update(deltaTime: number) {
  // Update game logic here
  // deltaTime is in milliseconds since last frame

  // Example: Rotate the player slowly
  const playerTransform = player.getComponent(TransformComponent);
  if (playerTransform) {
    playerTransform.rotation += 0.01;
  }
}

// Define the render function
function render() {
  // Clear the screen
  renderer.clear();

  // Render all actors in the world
  world.render(renderer);

  // Present the frame
  renderer.present();
}

// Register the update and render callbacks
engine.onUpdate(update);
engine.onRender(render);

// Start the game loop
engine.start();

console.log('Game loop started!');

import { InputManager, Keys } from '@nostalgi2d/engine';

// Create input manager
const input = new InputManager();
engine.setInputManager(input);

// Update the update function to handle input
function update(deltaTime: number) {
  const playerTransform = player.getComponent(TransformComponent);
  if (!playerTransform) return;

  const moveSpeed = 0.2; // pixels per millisecond

  // Handle WASD movement
  if (input.isKeyDown(Keys.W)) {
    playerTransform.position.y -= moveSpeed * deltaTime;
  }
  if (input.isKeyDown(Keys.S)) {
    playerTransform.position.y += moveSpeed * deltaTime;
  }
  if (input.isKeyDown(Keys.A)) {
    playerTransform.position.x -= moveSpeed * deltaTime;
  }
  if (input.isKeyDown(Keys.D)) {
    playerTransform.position.x += moveSpeed * deltaTime;
  }
}

import { 
  Engine, 
  World, 
  PixiRenderer,
  Actor,
  TransformComponent,
  SpriteComponent,
  InputManager,
  Keys
} from '@nostalgi2d/engine';

// Initialize engine
const engine = new Engine({
  targetFPS: 60,
  fixedTimestep: 1000 / 60,
  maxFrameSkip: 5
});

// Create world
const world = new World();
engine.setWorld(world);

// Set up renderer
const canvas = document.getElementById('game-canvas') as HTMLCanvasElement;
const renderer = new PixiRenderer(canvas, {
  width: 800,
  height: 600,
  backgroundColor: 0x0a0e1a
});
engine.setRenderer(renderer);

// Create input manager
const input = new InputManager();
engine.setInputManager(input);

// Create player
const player = new Actor('player');
const transform = new TransformComponent({
  position: { x: 400, y: 300 },
  rotation: 0,
  scale: { x: 1, y: 1 }
});
const sprite = new SpriteComponent({
  width: 32,
  height: 32,
  color: 0x00ffff
});
player.addComponent(transform);
player.addComponent(sprite);
world.addActor(player);

// Game loop
function update(deltaTime: number) {
  const playerTransform = player.getComponent(TransformComponent);
  if (!playerTransform) return;

  const moveSpeed = 0.2;

  if (input.isKeyDown(Keys.W)) {
    playerTransform.position.y -= moveSpeed * deltaTime;
  }
  if (input.isKeyDown(Keys.S)) {
    playerTransform.position.y += moveSpeed * deltaTime;
  }
  if (input.isKeyDown(Keys.A)) {
    playerTransform.position.x -= moveSpeed * deltaTime;
  }
  if (input.isKeyDown(Keys.D)) {
    playerTransform.position.x += moveSpeed * deltaTime;
  }
}

function render() {
  renderer.clear();
  world.render(renderer);
  renderer.present();
}

engine.onUpdate(update);
engine.onRender(render);
engine.start();
